INSERT INTO denominations (name, description, status) VALUES
('Catholicism-Roman Catholic', 'The Roman Catholic Church is the largest Christian church, led by the Pope, with a rich tradition of sacraments, liturgy, and global presence.', 'active'),
('Catholicism-Eastern Catholic', 'Eastern Catholic Churches are in full communion with the Pope but retain their own liturgical rites, traditions, and governance.', 'active'),
('Orthodoxy-Eastern Orthodox', 'The Eastern Orthodox Church emphasizes tradition, liturgy, and the seven ecumenical councils; it is organized into autocephalous regional churches.', 'active'),
('Orthodoxy-Oriental Orthodox', 'Oriental Orthodox Churches reject the Council of Chalcedon and maintain ancient Christian traditions, mainly in Egypt, Ethiopia, and Armenia.', 'active'),
('Protestantism-Anglican', 'The Anglican Church emerged from the English Reformation, blending Catholic liturgy with Protestant theology and episcopal governance.', 'active'),
('Protestantism-Lutheran', 'Lutheran churches follow the teachings of Martin Luther, emphasizing salvation by faith alone and the authority of Scripture.', 'active'),
('Protestantism-Presbyterian', 'Presbyterianism is a Reformed tradition governed by elders, emphasizing the sovereignty of God and covenant theology.', 'active'),
('Protestantism-Methodist', 'Methodism arose from the teachings of John Wesley, focusing on personal holiness, social justice, and structured spiritual practices.', 'active'),
('Protestantism-Baptist', 'Baptist churches emphasize believer''s baptism, congregational governance, and religious freedom.', 'active'),
('Protestantism-Reformed', 'Reformed churches follow Calvinist theology, stressing God''s sovereignty, predestination, and covenantal living.', 'active'),
('Protestantism-Pentecostal', 'Pentecostalism emphasizes the gifts of the Holy Spirit, including speaking in tongues, prophecy, and healing.', 'active'),
